__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night fast-on or vent-on sets bedside and mirror",
    "parent": 0,
    "comment": "If mirror fast-on (user control) during night or vent turns on, set bedside and mirror settings",
    "if": [
      { "(": 1 },
        { "==": { "device": "4F 3E 2 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "50 9C B6 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "1A F6 B 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "4F 3E 2 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "4F 3E 2 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Combine a user change-of-control (mirror DFON) or a vent change-of-state (vent ST == On) limited to the overnight period. On trigger, immediately set bedside lamp to 30% (DON with 30%) and configure mirror ramp rate to 1.0 minute (RR enum value 12) and set its on-level to 60% (OL 60%)."
  }
}
__END_NUCORE_ROUTINE__
