__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday 06:30 Query - Downstairs Associated Devices",
    "parent": 0,
    "comment": "Poll both downstairs association devices every weekday morning",
    "if": [
      { "weekly": { "days":"mon,tue,wed,thu,fri", "from": { "time":"06:30", "for": { "hours":0, "minutes":1, "seconds":0 } } } }
    ],
    "then": [
      { "device":"ZY007_254N001", "command":"QUERY", "parameters":[] },
      { "device":"ZY007_254N002", "command":"QUERY", "parameters":[] }
    ],
    "else": [],
    "reasoning": "Run a short weekly trigger at 06:30 on weekdays and send QUERY to both downstairs association devices to refresh their reported state. Parameters are empty because QUERY accepts no parameters in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
