__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Nightly all-off 22:00-06:00",
    "parent": 0,
    "comment": "Turn all switches off overnight",
    "if": [
      { "from": { "time":"22:00", "to": { "time":"06:00", "day":1 } } }
    ],
    "then": [
      {
        "device": "ZY002_183",
        "command": "ALLOFF",
        "parameters": []
      }
    ],
    "else": [],
    "reasoning": "Schedule triggers nightly between 22:00 and 06:00 (overnight). When active, send ALLOFF to the Switch All device (ZY002_183) to ensure all linked circuits are turned off overnight. No other devices accept a direct off command in the provided structure, so ALLOFF is used on the Switch All device."
  }
}
__END_NUCORE_ROUTINE__
