__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Lock ratgdo at sunset when front door closed",
    "parent": 0,
    "comment": "",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n010_28259168", "status": "GV4", "value": 100, "uom": "79", "precision": 0 } }
    ],
    "then": [
      { "device":"n018_eh9834cfaf","command":"LOCK","parameters":[] }
    ],
    "else": [
      { "device":"n018_eh9834cfaf","command":"UNLOCK","parameters":[] }
    ],
    "reasoning": "At local sunset check Front Door OLD Door Status (GV4). If Closed (100) send LOCK to the ratgdov25i device (n018_eh9834cfaf). If not Closed, send UNLOCK. Uses only defined devices, properties, commands, and uom ids from DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
