__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset — Kitchen Island auto-on to 60% with 1min ramp",
    "parent": 0,
    "comment": "At sunset, ensure Kitchen Island lights are on to a comfortable level with a 1.0 minute ramp if currently off.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "4F A0 9A 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "4F A0 9A 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "4F A0 9A 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger at local sunset. Confirm Kitchen Island is currently Off (ST=0) to avoid redundant commands. First set Ramp Rate (RR) to enum value 12 (1.0 minute) using uom 25, then send DON with 60% (uom 51). Ordering ensures the ramp rate is applied before turning on."
  }
}
__END_NUCORE_ROUTINE__
