__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening: foyer DFON sets dining south to 60% with 1min ramp",
    "parent": 0,
    "comment": "Triggered by fast-on (DFON) of foyer during weekdays 18:00-20:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "1D C9 4A 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "20:00" } } } }
    ],
    "then": [
      { "device": "1D CE 88 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] },
      { "device": "1D CE 88 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on the foyer grouped with a weekday evening schedule to avoid accidental triggers; then set dining south brightness to 60% (DON param UOM 51) and set its Ramp Rate via RR enum value 12 (1.0 minute)."
  }
}
__END_NUCORE_ROUTINE__
