__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night quick-on sets plant ledge to 20% and stops fades",
    "parent": 0,
    "comment": "COC on stairs fast-on during late night lowers plant ledge and halts fades",
    "if": [
      { "==": { "device": "ZY005_1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY004_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ] },
      { "device": "ZY004_1", "command": "FDSTOP", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC (DFON) from stairs during overnight window to immediately set Plant Ledge to low brightness (20%) and send FDSTOP to ensure no active fades continue. Keeps logic simple and device-specific."
  }
}
__END_NUCORE_ROUTINE__
