__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Fast-On Front/Turret -> Eaves ON + Front Porch BL 50% (20:00-23:00)",
    "parent": 0,
    "comment": "Trigger from COC (Fast On) on Front Porch or Turret during evening window",
    "if": [
      { "(": 1 },
        { "==": { "device": "21 F 97 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "21 B 80 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "20:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "21 D 9D 1", "command": "DON", "parameters": [] },
      { "device": "21 F 97 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A COC DFON (Fast On) on either Front Porch or Turret between 20:00 and 23:00 should immediately turn the Eaves relay on and set the Front Porch backlight to a comfortable 50% for visibility. The routine groups the two COC triggers with OR and limits execution to the daily time window."
  }
}
__END_NUCORE_ROUTINE__
