__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight DFON sync: set other light to 50% with 1min ramp",
    "parent": 0,
    "comment": "When switch 53 E0 E5 1 is fast-on overnight, set 53 F0 7F 1 to 50% with RR=1.0min. Otherwise turn 53 F0 7F 1 fully on.",
    "if": [
      { "==": { "device": "53 E0 E5 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "53 F0 7F 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "53 F0 7F 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "53 F0 7F 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "A fast-on (COC) from the user triggers an immediate reaction. If it occurs overnight use a lower level and a gentle 1.0-minute ramp (RR=12) for comfort; otherwise simply turn the other light fully on. Commands and parameter UOMs/enum values are taken directly from the device structure."
  }
}
__END_NUCORE_ROUTINE__
