__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekend evening auto-brighten Address Sign",
    "parent": 0,
    "comment": "If sign is dim or manually turned on during weekend evenings, set to warm white and bright.",
    "if": [
      { "(": 1 },
        { "<": { "device": "n020_huegrp88", "status": "ST", "value": 20, "uom": "51", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "n020_huegrp88", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "sat,sun", "from": { "time": "18:00", "to": { "time": "23:59" } } } }
    ],
    "then": [
      {
        "device": "n020_huegrp88",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "Color Temperature", "value": 4000, "uom": "26", "precision": 1 },
          { "name": "Transition Time", "value": 1000, "uom": "42", "precision": 1 },
          { "name": "Brightness", "value": 200, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Weekend evenings are when the sign should be prominent. Trigger on either the sign being dim (ST < 20%) or a physical ON (DON). Restrict to weekend evenings using weekly schedule. Use SET_CTBR with explicit color temperature and brightness, using the device's defined UOMs and precisions."
  }
}
__END_NUCORE_ROUTINE__
