__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday morning fast-on sets secondary backlight",
    "parent": 0,
    "comment": "When 4D 34 1D 3 receives DFON during weekday morning window, set backlight on 4D 34 1D 4 and write changes on origin keypad.",
    "if": [
      { "==": { "device": "4D 34 1D 3", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "4D 34 1D 4", "command": "BL", "parameters": [ { "name": "n/a", "value": 10, "uom": "25", "precision": 0 } ] },
      { "device": "4D 34 1D 3", "command": "WDU" }
    ],
    "else": [],
    "reasoning": "Use COC (DFON) from device 4D 34 1D 3 combined with a weekday morning schedule to only change the secondary keypad backlight when the user intentionally fast-ons the primary keypad in the morning. BL uses uom 25 and integer precision; WDU commits the change."
  }
}
__END_NUCORE_ROUTINE__
