__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Alarm On -> Fan A ramp and on",
    "parent": 0,
    "comment": "When the temp alarm is On, increase ventilation by setting fan A ramp rate to 1.0 min and turning it on to 70%.",
    "if": [
      { "==": { "device": "ZY104_002_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "48 E 86 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "48 E 86 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on GAR4 status = On (value 100). Set KDR4-FAN LTS-A_SW ramp rate to enum value 12 (1.0 minute) then turn it on to 70% to ventilate." 
  }
}
__END_NUCORE_ROUTINE__
