__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on charger when Keep Awake is enabled on weekdays",
    "parent": 0,
    "comment": "Enable charger when sensor Keep Awake is physically activated during work hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY171_199", "control": "DON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZM00004_001_1", "command": "DON" }
    ],
    "else": [
      { "device": "ZM00004_001_1", "command": "DOF" }
    ],
    "reasoning": "Use COC from ZY171_199 (Keep Awake sent as DON) combined with a weekday schedule to enable the charger during active hours; otherwise ensure charger is off. DON used for charger since it is an accepted command; DOF guarantees off state in else."
  }
}
__END_NUCORE_ROUTINE__
