__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night: Garage TV ON turns on Basement Reset",
    "parent": 0,
    "comment": "Enable basement reset when garage TV is turned on between 22:00 and 06:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZM00008_001_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time":"22:00", "to": { "time":"06:00", "day":1 } } }
    ],
    "then": [
      { "device": "ZM00007_001_1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Use a COC (DON) from the garage TV during nighttime (22:00-06:00) to turn on the basement reset only at night. This avoids activating during daytime button presses or state changes."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Night: Garage TV OFF turns off Basement Reset",
    "parent": 0,
    "comment": "Disable basement reset when garage TV is turned off between 22:00 and 06:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZM00008_001_1", "control": "DOF" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time":"22:00", "to": { "time":"06:00", "day":1 } } }
    ],
    "then": [
      { "device": "ZM00007_001_1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "Mirror the garage TV off control (COC DOF) during the same nighttime window to turn the basement reset off. Keeps states consistent at night."
  }
}
__END_NUCORE_ROUTINE__
