__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday triple-press queries responder",
    "parent": 0,
    "comment": "Run responder query only when Button 1 triple-press occurs during business hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY143_201N001", "control": "DON3" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY143_259", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Only allow manual scene activation via Button 1 triple-press during weekdays 09:00-17:00; the routine issues a Query to the scene responder to trigger/update the scene state."
  }
}
__END_NUCORE_ROUTINE__
