__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Smoke machine ON during work hours - query alarms",
    "parent": 0,
    "comment": "When smoke machine is turned on manually during weekday work hours, poll related devices.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY017_252N003", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY021_309", "command": "QUERY", "parameters": [] },
      { "device": "ZY021_251", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "COC: detect manual ON (DON) from the smoke machine, restricted to weekday work hours; then query the power alarm and garage control to get their current states for situational awareness."
  }
}
__END_NUCORE_ROUTINE__
