__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night - Turn on master bath light when garage opens",
    "parent": 0,
    "comment": "When garage door opens at night, set light to 80% with 30s ramp",
    "if": [
      { "==": { "device": "ZY026_379", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "53 E0 DC 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 18, "uom": "25", "precision": 0 } ] },
      { "device": "53 E0 DC 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on garage sensor COS to open (ST -> On) only during night window. Apply a 30s ramp rate (RR value 18) then send DON at 80% to provide quick but gentle illumination."
  }
}
__END_NUCORE_ROUTINE__
