__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Lamp on to 60% if currently off",
    "parent": 0,
    "comment": "Turn FR2-TOLL LAMP on at sunset only when it is off",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "26 74 87 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "26 74 87 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "At local sunset, only power the lamp to 60% when its status shows Off (0) to avoid overriding a manual or prior state. Uses device 26 74 87 1 DON with a percent parameter.">
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Physical fast-on sets lamp to full",
    "parent": 0,
    "if": [
      { "==": { "device": "26 74 87 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "26 74 87 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A user-initiated Fast On (DFON) is a Change of Control implying intent to maximize brightness; set On Level (OL) to 100% on device 26 74 87 1."
  }
}
__END_NUCORE_ROUTINE__
