__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn on fan when tamper alarm triggered between sunset and 23:00",
    "parent": 0,
    "comment": "Trigger on COS of tamper alarm during evening window",
    "if": [
      { "==": { "device":"ZY189_157","status":"ST","value":100,"uom":"78","precision":0 } },
      { "and": 1 },
      { "from": { "sunset": 0, "to": { "time":"23:00","day":0 } } }
    ],
    "then": [
      { "device":"ZY104_001_1","command":"DON","parameters":[] }
    ],
    "else": [],
    "reasoning": "When the tamper alarm (ZY189_157) reports a COS to On (value 100) during the sunset-to-23:00 window, the external fan (ZY104_001_1) should be turned on to respond immediately. Outside that window no action is taken by this routine."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Daily fan off at 23:00",
    "parent": 0,
    "comment": "Ensure fan is off nightly",
    "if": [
      { "at": { "time": "23:00" } }
    ],
    "then": [
      { "device":"ZY104_001_1","command":"DOF","parameters":[] }
    ],
    "else": [],
    "reasoning": "A scheduled daily off at 23:00 ensures the fan is turned off every night regardless of triggers."
  }
}
__END_NUCORE_ROUTINE__
