__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Motion ON triggers Holidays Re-Discover evening",
    "parent": 0,
    "comment": "Run DISCOVER on holidays controller when motion ON during evening window",
    "if": [
      { "==": { "device": "ZY188_380", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n004_controller", "command": "DISCOVER", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use the motion sensor's send event DON as a COC trigger and restrict with a nightly time window; DISCOVER is an accepted command for n004_controller."
  }
}
__END_NUCORE_ROUTINE__
