__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening double-tap: set family dimmer 60% and dining relay on",
    "parent": 0,
    "comment": "Trigger on physical Fast On (DFON) on Corner Table during 18:00-23:00",
    "if": [
      { "==": { "device": "1A E9 3 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "23 C1 72 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] },
      { "device": "22 6E A3 1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "User requested a routine tied to a physical control (COC) and limited to an evening window. The Corner Table DFON is used as the COC trigger; when it occurs between 18:00-23:00 the Family Room dimmer is set to 60% (using its DON parameter range 0-100, uom 51) and the Dinning Room relay is turned on (DON). No actions otherwise." 
  }
}
__END_NUCORE_ROUTINE__
