__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Garage motion or tamper -> Gym blue evening on",
    "parent": 0,
    "comment": "Turn gym lights blue at medium brightness when motion starts or tamper fast-on between 18:00-23:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "55 89 5D D", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "55 89 5D 10", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n012_gym", "command": "SET_COLOR", "parameters": [ { "name": "n/a", "value": 5, "uom": "25", "precision": 0 } ] },
      { "device": "n012_gym", "command": "SETB", "parameters": [ { "name": "n/a", "value": 32768, "uom": "56", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "User wants lights ON when motion starts or tamper fast-on between 18:00-23:00; use a grouped trigger (motion COS or tamper COC) combined with a time window. Actions set color to Blue (enum value 5, uom 25) and set brightness to mid-scale (32768 on 0-65535, uom 56)."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1002,
    "name": "Garage motion off -> Gym off evening",
    "parent": 0,
    "comment": "Turn gym lights off when garage motion stops during 18:00-23:00",
    "if": [
      { "==": { "device": "55 89 5D D", "status": "ST", "value": 0, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n012_gym", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When Garage Motion Enabled reports Off (COS) during the same evening window, turn the Gym group off to conserve power."
  }
}
__END_NUCORE_ROUTINE__
