__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday Master Closet ON -> Palm Lights 1 ON",
    "parent": 0,
    "comment": "Turn Palm Lights 1 on when Master Closet Disco is actuated ON during weekday work hours; otherwise turn them off.",
    "if": [
      { "==": { "device": "ZY009_252N002", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY027_001_1", "command": "DON" }
    ],
    "else": [
      { "device": "ZY027_001_1", "command": "DOF" }
    ],
    "reasoning": "Use the COC event (DON) emitted by Master Closet Disco and restrict to weekday 09:00-17:00. When triggered, issue DON to Palm Lights 1. If not matched, ensure lights are off to save power."
  }
}
__END_NUCORE_ROUTINE__
