__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion queries alarm states",
    "parent": 0,
    "comment": "When motion turns on during night query alarms",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY028_155", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"ZY028_310","command":"QUERY","parameters":[] },
      { "device":"ZY028_309","command":"QUERY","parameters":[] }
    ],
    "else": [],
    "reasoning": "Query the power and system alarms whenever the motion sensor reports ON during the night period 22:00-06:00. Using COS on motion sensor ST==100 as trigger; Query commands are the only accepted actions on those devices."
  }
}
__END_NUCORE_ROUTINE__
