__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Foyer KP.D DFON backlight max",
    "parent": 0,
    "comment": "When Foyer KP.D emits DFON set backlight to 127",
    "if": [
      { "==": { "device":"51 73 57 4", "control":"DFON" } }
    ],
    "then": [
      { "device":"51 73 57 4", "command":"BL", "parameters":[{ "name":"n/a","value":127,"uom":"25","precision":0 }] },
      { "device":"51 73 57 4", "command":"WDU", "parameters":[] }
    ],
    "else": [],
    "reasoning": "Use DFON COC from device 51 73 57 4 to set backlight via BL; call WDU to commit the change."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Foyer KP.D sunset backlight",
    "parent": 0,
    "comment": "At sunset set a moderate backlight on KP.D",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device":"51 73 57 4", "command":"BL", "parameters":[{ "name":"n/a","value":10,"uom":"25","precision":0 }] },
      { "device":"51 73 57 4", "command":"WDU", "parameters":[] }
    ],
    "else": [],
    "reasoning": "Scheduled routine triggers at sunset and uses BL (uom 25) to set a moderate backlight; WDU commits the change."
  }
}
__END_NUCORE_ROUTINE__
