__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night alarm or night button -> both lights warm full",
    "parent": 0,
    "comment": "Respond to alarm COS or Guest Room COC during night",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY028_1", "status": "ALARM", "value": 0, "uom": "25", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "n001_880109c1fa690b", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n001_880109c1fa690b", "command": "SET_CTBR", "parameters": [ { "name": "K", "value": 2000, "uom": "26", "precision": 1 }, { "name": "D", "value": 5000, "uom": "42", "precision": 1 }, { "name": "BR", "value": 254, "uom": "100", "precision": 1 } ] },
      { "device": "n001_880109c1fa540b", "command": "SET_CTBR", "parameters": [ { "name": "K", "value": 2000, "uom": "26", "precision": 1 }, { "name": "D", "value": 5000, "uom": "42", "precision": 1 }, { "name": "BR", "value": 254, "uom": "100", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Trigger when the alarm reports ALARM == True (enum value 0) or when the Guest Room nightstand receives a Fast On (COC DFON), but only during night hours (22:00-06:00). Then set both lights to a warm 2000K color at maximum brightness using a 5 second transition for visibility and smoothness."
  }
}
__END_NUCORE_ROUTINE__
