__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn off Soffit at Night When Rain Rate > 0.50 in/hr",
    "parent": 0,
    "comment": "Disable soffit during nighttime heavy rain",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { ">": { "device": "n003_118680", "status": "RAINRT", "value": 50, "uom": "24", "precision": 2 } }
    ],
    "then": [
      { "device": "n001_88010cff475e0b", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Only act during the defined night window (22:00-06:00). Use the ST-00110502 Rain Rate (RAINRT) in inches/hour (uom 24). A threshold of 0.50 in/hr is represented as value=50 with precision=2. When the condition is met, send Off (DOF) to the Soffit device to prevent running lights in heavy rain."
  }
}
__END_NUCORE_ROUTINE__
