__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening: double-tap Wine Lamp -> set Floor Lamp 30% with 1min ramp",
    "parent": 0,
    "comment": "Double-tap DFON during weekdays 21:00-23:00 sets floor lamp and ramp rate",
    "if": [
      { "==": { "device": "2B A5 1E 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "21:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "2B C6 5A 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "2B C6 5A 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Separate routine triggered by the DFON control (tapCount=2) within the specified weekly time window. Then sets the Living Dinig Room Floor Lamp brightness to 30% (DON with uom 51) and sets Ramp Rate to the enum value for 1.0 minute (RR parameter value 12, uom 25)."
  }
}
__END_NUCORE_ROUTINE____BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Single-tap Wine Lamp fast-on -> turn Wine Lamp off",
    "parent": 0,
    "comment": "Single DFON tap turns the Wine Lamp off",
    "if": [
      { "==": { "device": "2B A5 1E 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 1, "uom": "108", "precision": 0 } ] } }
    ],
    "then": [
      { "device": "2B A5 1E 1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "COC: detect a single DFON tap on the Family Room Wine Lamp (tapCount=1) and immediately send DOF to turn that lamp off."
  }
}
__END_NUCORE_ROUTINE__
