__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night fast-on: powder room set level and quick ramp",
    "parent": 0,
    "comment": "When the powder room is fast-on at night, set modest level and fast ramp",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "1D D1 1F 1", "control": "DFON" } }
    ],
    "then": [
      {
        "device": "1D D1 1F 1",
        "command": "OL",
        "parameters": [
          { "name": "n/a", "value": 30, "uom": "51", "precision": 2 }
        ]
      },
      {
        "device": "1D D1 1F 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 18, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Schedule limits action to nighttime (22:00–06:00). Trigger uses COC 'DFON' so the routine runs when a fast-on control is performed. Then sets On Level to 30% (uom 51) and Ramp Rate to the 30.0 seconds enum index (value 18, uom 25)."
  }
}
__END_NUCORE_ROUTINE__
