__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Island double-tap after sunset: set ramp and turn on",
    "parent": 0,
    "comment": "Fast On after sunset sets ramp then turns on to 80%",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "55 CB 20 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "55 CB 20 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "55 CB 20 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "User requested action only after sunset when a Fast On control occurs on the island device. The routine first sets Ramp Rate to the 1.0 minute enum (value 12, uom 25) then issues an On (DON) with 80% using uom 51."
  }
}
__END_NUCORE_ROUTINE__
