__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Close Shades When Cabinet Top Fast-On Overnight",
    "parent": 0,
    "comment": "Close Shades Middle when Cabinet Top emits a DFON during night hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "4D 44 E2 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n001_775288ab", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (Fast On DFON) from the Cabinet Top and restrict it to the overnight window so shades close only at night when the outlet is fast-turned on."
  }
}
__END_NUCORE_ROUTINE__
