{
  "routine": {
    "id": 1001,
    "name": "Weeknight double-tap sets sofa area to 40%",
    "parent": 0,
    "comment": "Double-tap Ceiling.Lights DFON between 18:00-23:00 weekdays triggers SofaAreaLights",
    "if": [
      { "==": { "device": "70 37 C0 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "70 42 BD 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 40, "uom": "51", "precision": 0 } ] },
      { "device": "70 42 BD 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 28, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Physical double-tap (COC DFON with tapCount=2) during weekday evening hours sets SofaAreaLights to a comfortable 40% and applies a fast ramp (0.5s enum index 28) for immediate response. Weekly window limits to weekdays 18:00-23:00."
  }
}
__END_NUCORE_ROUTINE__
