__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday evening: GAR TV manual ON -> turn on basement reset",
    "parent": 0,
    "comment": "When the garage TV (manual ON) during weekdays 18:00-23:00, send On to basement reset",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZM00008_001_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZM00007_001_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (manual ON) for the garage TV combined with a weekday evening weekly schedule; action is to send DON to the basement reset device which accepts DON. No parameters required."
  }
}
__END_NUCORE_ROUTINE__
