__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Weekday fast-press updates responder",
    "parent": 0,
    "comment": "Query responder when either scene button fast-on pressed during work hours",
    "if": [
      { "(: 1 },
      { "==": { "device": "ZY143_201N001", "control": "DFON" } },
      { "or": 1 },
      { "==": { "device": "ZY143_201N002", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "ZY143_259", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC triggers (DFON) from either scene button, limited to weekday business hours to avoid accidental night triggers. The routine queries the Responder to refresh scene state when triggered."
  }
}
__END_NUCORE_ROUTINE__
