__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query humidity when party lasers turn on overnight",
    "parent": 0,
    "comment": "Triggered by either laser ON event during overnight window",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY011_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY011_252N003", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"ZY028_373","command":"QUERY","parameters":[] }
    ],
    "else": [],
    "reasoning": "When either party laser is manually turned on during the overnight period (22:00-06:00), query the humidity sensor to capture and log the current humidity for monitoring and diagnostics. Using the lasers' DON send-events as the change-of-control trigger ensures queries occur only when lights are physically activated."
  }
}
__END_NUCORE_ROUTINE__
