__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight: SmokeMachine ON -> Query PowerAlarm",
    "parent": 0,
    "comment": "When smoke machine sends DON between 22:00-06:00, query power alarm",
    "if": [
      { "==": { "device": "ZY017_252N003", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY021_309", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Separate routine triggers on the smoke machine DON event during overnight window; action uses the only accepted command (QUERY) for the Power Alarm device per DEVICE STRUCTURE. Kept minimal and deterministic."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Overnight: SmokeMachine OFF -> Query WorkbenchControl",
    "parent": 0,
    "comment": "When smoke machine sends DOF between 22:00-06:00, query workbench control",
    "if": [
      { "==": { "device": "ZY017_252N003", "control": "DOF" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY021_251", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Separate routine triggers on the smoke machine DOF event during overnight hours and sends a QUERY to the Workbench Control device, using only commands defined in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
