__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Nighttime manual on sets can lights to 30%",
    "parent": 0,
    "comment": "When the KP 2.B is physically turned on at night, set parent can lights to a low level.",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"52 5A 7C 2", "control":"DFON" } }
    ],
    "then": [
      { "device":"52 5A 7C 1","command":"OL","parameters":[{ "name":"n/a","value":30,"uom":"51","precision":0 }] }
    ],
    "else": [],
    "reasoning": "Use a COC (physical DFON) on the KP 2.B during late-night hours to set the overall can lights (parent device) to a low on-level (30%) so manual activation is dim and not blinding. The parent device accepts OL with a 0-100 % parameter (uom 51)."
  }
}
__END_NUCORE_ROUTINE__
