  "routine": {
    "id": 101,
    "name": "Overnight: KP 1.B COC DFON -> isolate on",
    "parent": 0,
    "comment": "When KP 1.B is physically turned on overnight, keep it on and force others off",
    "if": [
      { "==": { "device": "51 73 9C 2", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "51 73 9C 2", "command": "DON" },
      { "device": "51 73 9C 3", "command": "DOF" },
      { "device": "51 73 9C 4", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "Separate routine triggered by COC (DFON) for KP 1.B during overnight period to act only when user physically turns that switch on; ensure exclusivity by turning other fixtures off."
  }
}
__END_NUCORE_ROUTINE__

