__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Fast-On sync: motor Medium + light 70%",
    "parent": 0,
    "comment": "When Gym Fan-Light receives a Fast On (DFON) physically, set motor and light levels",
    "if": [
      { "==": { "device": "51 CD 91 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "51 CD 91 2", "command": "DON", "parameters": [ { "name": "n/a", "value": 75, "uom": "51", "precision": 0 } ] },
      { "device": "51 CD 91 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use the physical Fast On (COC) on the Gym Fan-Light (device 51 CD 91 1) to synchronize the Gym Fan-Motor (51 CD 91 2) to Medium (value 75, uom 51) and set the Gym Fan-Light on-level to 70% (uom 51). Commands used exist in each device's accepts list; parameter names follow the device definition (n/a)."
  }
}
__END_NUCORE_ROUTINE__
