__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Open Shades at Sunset-10min when Top Outlet Fast-On",
    "parent": 0,
    "comment": "Open Shades Middle when the top outlet receives a fast-on control shortly before sunset.",
    "if": [
      { "at": { "sunset": -600 } },
      { "and": 1 },
      { "==": { "device": "4D 44 E2 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "n001_775288ab", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a schedule trigger at sunset minus 10 minutes combined with a COC (fast-on) from the top outlet. When both occur, send the Shade Open command (DON) to Shades Middle. No action on else."
  }
}
__END_NUCORE_ROUTINE__
