__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening: turn tool room lights on when front door fan switch is turned on",
    "parent": 0,
    "comment": "When ZM00011_001_1 is physically turned on between 18:00 and 23:00, send DON to ZY143_1",
    "if": [
      { "==": { "device": "ZM00011_001_1", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "ZY143_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (== control DON) on the front door fan switch so the action triggers on a physical on action. Restrict to an evening time window to avoid triggering at other times. Action sends DON to the tool room lights which accept DON."
  }
}
__END_NUCORE_ROUTINE__
