__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning fast-press queries",
    "parent": 0,
    "comment": "Query responder on weekday fast-press morning; otherwise query button",
    "if": [
      { "==": { "device": "ZY143_201N001", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:30", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "ZY143_259", "command": "QUERY", "parameters": [] }
    ],
    "else": [
      { "device": "ZY143_201N001", "command": "QUERY", "parameters": [] }
    ],
    "reasoning": "Fast On (DFON) from Button 1 during weekday mornings triggers a query of the scene responder for status; fast-presses outside that window should query the originating button for diagnostics."
  }
}
__END_NUCORE_ROUTINE__
