__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday morning cool-down when hot",
    "parent": 0,
    "comment": "If morning temperature high on weekdays, enable cooling and set point",
    "if": [
      { "weekly": { "days":"mon,tue,wed,thu,fri","from":{ "time":"08:00","to":{ "time":"09:00" } } } },
      { "and": 1 },
      { ">=": { "device":"n001_t417350996561","status":"ST","value":75,"uom":"17","precision":1 } }
    ],
    "then": [
      { "device":"n001_t417350996561","command":"CLIMD","parameters":[{ "name":"n/a","value":2,"uom":"67","precision":0 }] },
      { "device":"n001_t417350996561","command":"CLISPC","parameters":[{ "name":"n/a","value":72,"uom":"17","precision":1 }] }
    ],
    "else": [],
    "reasoning": "During weekday mornings (08:00-09:00) if the dining temperature is >=75°F, switch to Cool (enum 2) and set the cool setpoint to 72°F to quickly reduce temperature."
  }
}
__END_NUCORE_ROUTINE__
