__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening Fast-On: switch on and set dimmer to 80%",
    "parent": 0,
    "comment": "When Kitchen Dimmer Fast On (physical control) occurs between 18:00 and 23:00, enable main power switch and set dimmer to 80%.",
    "if": [
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { "==": { "device": "ZY003_1", "control": "DFON" } }
    ],
    "then": [
      { "device": "ZY002_1", "command": "DON", "parameters": [] },
      { "device": "ZY003_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "The user asked to trigger automation on a physical Fast On (COC) of the Kitchen Dimmer but only during evening hours. The schedule token restricts timing. The routine turns the On-Off Power Switch on and sends a DON to the Kitchen Dimmer with a percentage parameter (uom 51) to set 80%."
  }
}
__END_NUCORE_ROUTINE__
