__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Stairs fast-on -> Plant Ledge 60% and stop fade",
    "parent": 0,
    "comment": "When Stairs.Dimmer fast-on is triggered, set Plant Ledge to 60% and stop fading.",
    "if": [
      { "==": { "device": "ZY005_1", "control": "DFON" } }
    ],
    "then": [
      { "device": "ZY004_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 2 } ] },
      { "device": "ZY004_1", "command": "FDSTOP" }
    ],
    "else": [],
    "reasoning": "Treating DFON on Stairs.Dimmer as a COC (manual fast on). Immediately command Plant Ledge to a stable 60% and send FDSTOP to ensure no ongoing fades override the set level. Uses only devices and commands defined in DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
