__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Keep Wake Awake when assoc on during weekday morning",
    "parent": 0,
    "comment": "Triggered by assoc On events during weekday mornings",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY028_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY028_252N003", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "ZY028_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY028_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 2, "uom": "20", "precision": 0 } ] }
    ],
    "else": [
      { "device": "ZY028_199", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "When either association device sends an On (COC) during weekday mornings, enable Keep Awake (DON) and set Wake Interval to 2 hours using hours UOM; otherwise ensure Keep Awake is turned off." 
  }
}
__END_NUCORE_ROUTINE__
