__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday: device 004 ON -> query 005",
    "parent": 0,
    "comment": "When device 004 is physically turned on during weekdays, query device 005",
    "if": [
      { "==": { "device": "ZY028_252N004", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "ZY028_252N005", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (control DON) from device ZY028_252N004 limited to weekdays 08:00-18:00; then send a QUERY to ZY028_252N005 to refresh its state. Commands use only accepted 'QUERY' from the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
