__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Bamboo warm dim if reachable and low",
    "parent": 0,
    "comment": "Dim planter at sunset if reachable and currently dim",
    "if": [
      { "at": { "sunset": -600 } },
      { "and": 1 },
      { "==": { "device": "n020_88010d1813d20b", "status": "GV6", "value": 1, "uom": "2", "precision": 0 } },
      { "and": 1 },
      { "<": { "device": "n020_88010d1813d20b", "status": "ST", "value": 50, "uom": "51", "precision": 0 } }
    ],
    "then": [
      {
        "device": "n020_88010d1813d20b",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 5000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 150, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Trigger at sunset-10min; require the planter to be reachable (GV6==1) and currently below 50% brightness. Use SET_CTBR to set color temp 3000K, 5s transition (5000ms), brightness 150."
  }
}
__END_NUCORE_ROUTINE__
