__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "KP 1.B Night DFON -> Backlight 80",
    "parent": 0,
    "comment": "When KP 1.B receives a fast-on control overnight set backlight high, else clear it.",
    "if": [
      { "==": { "device": "51 73 9C 2", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      {
        "device": "51 73 9C 2",
        "command": "BL",
        "parameters": [
          { "name": "n/a", "value": 80, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "else": [
      {
        "device": "51 73 9C 2",
        "command": "BL",
        "parameters": [
          { "name": "n/a", "value": 0, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "reasoning": "Use a COC (DFON) to detect the physical fast-on action on KP 1.B and restrict to overnight hours. Then set the device's backlight using its BL accept command. Else branch clears the backlight."
  }
}
__END_NUCORE_ROUTINE__
