__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday morning: Closet-on triggers Bath-on and query fireplace",
    "parent": 0,
    "comment": "",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY009_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "ZY008_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY024_251", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the Master Closet is physically turned on (COC) during weekday mornings, enable the Master Bath (uses DON with enum uom 78 value 100) and query the fireplace controller to synchronize or verify state. Weekly restriction limits to desired hours."
  }
}
__END_NUCORE_ROUTINE__
